import { useState, useEffect } from 'react';
import './App.css';

const API_KEY = "5b9d75e6acc14d4b073c8f9eecb25290";

function App() {

  const [results, setResults] = useState([])

  const [longitude, setLongitude] = useState("")
  const [latitude, setLatitude] = useState("")

  useEffect(() => {
    getLocation()

    fetchWeatherData()
  });

  var x = document.getElementById("demo");
  function getLocation() {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(showPosition);
    } else { 
      x.innerHTML = "Geolocation is not supported by this browser.";
    }
  }

  function showPosition(position) {
    setLongitude(String(position.coords.latitude))
    setLatitude(String(position.coords.longitude))
  }


  const fetchWeatherData = () => {
    fetch(`api.openweathermap.org/data/2.5/weather?lat=${latitude}&lon=${longitude}&appid=${API_KEY}`)
    .then((response) => response.json())
    .then((data) => {
      console.log(data);
      setResults(data);
    })
  }
  return (
    <div className="App">
      <div id="demo">
      <h1></h1>

      <h2></h2>

      </div>

    </div>
  );
}

export default App;
