import { useState, useEffect } from "react";
import "./App.css";
import ReactDOM from "react-dom";

const API_KEY = "09ac365afdba71cc5a62d2f4cf8f91aa";

function App() {
  const [results, setResults] = useState([]);
  const [longitude, setLongitude] = useState("");
  const [latitude, setLatitude] = useState("");

  const [isFahrenheit, setIsFahrenheit] = useState(true)

  useEffect(() => {
    // getLocation()
    // fetchWeatherData()
  });

  var x = document.getElementById("demo");
  function getLocation() {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(showPosition);
      console.log(longitude);
    } else {
      x.innerHTML = "Geolocation is not supported by this browser.";
    }
  }



  function showPosition(position) {
    setLongitude(String(position.coords.latitude));
    console.log(position.coords.latitude);
    setLatitude(String(position.coords.longitude));
  }

  const convertToFahrenheit = (kelvinTemp) => {
    return ((((kelvinTemp - 273.15) * 9 ) / 5) + 32).toFixed(2)
  }

  const getMeasurement = () => {
    if (isFahrenheit) {
      return "Fahrenheit"
    } else {
      return "Celcius"
    }
  }
  // api.openweathermap.org/data/2.5/weather?id=2172797&appid=5b9d75e6acc14d4b073c8f9eecb25290
  const fetchWeatherData = () => {
    fetch(
      `https://api.openweathermap.org/data/2.5/weather?lat=35&lon=139&appid=${API_KEY}`
    )
      .then((response) => response.json())
      .then((data) => {
        const dataPoint = { temp: data.main.temp, city: data.name, icon: data.weather[0].icon };
        setResults(dataPoint);
        console.log(results);
      });
  };
  return (
    <div className="App">
      <div id="demo">
        {results.length !== 0 && <img src={`http://openweathermap.org/img/wn/${results.icon}@2x.png`} alt="Logo" />}
        <h1>{results.city}</h1>
        <h2>{convertToFahrenheit(results.temp)} {getMeasurement()}</h2>
      </div>

      <button type="button" onClick={() => fetchWeatherData()}>
        Get Weather
      </button>
    </div>
  );
}

export default App;
